const DB_KEY = "quartz_checklists_v1";

const LAST_NL_KEY = "quartz_last_nl_control_v1";

function getLastNL(){
  return (localStorage.getItem(LAST_NL_KEY) || "").trim();
}
function setLastNL(v){
  localStorage.setItem(LAST_NL_KEY, (v || "").toString().trim());
}

function slugifyId(name){
  return (name || "")
    .toString()
    .normalize("NFD").replace(/[\u0300-\u036f]/g, "")
    .replace(/[^a-zA-Z0-9]+/g, "_")
    .replace(/^_+|_+$/g, "")
    .toUpperCase() || "CHECKLIST";
}

function makeUniqueId(baseId){
  let id = baseId;
  let i = 2;
  while(state.list.some(x => x.id === id)){
    id = `${baseId}_${i++}`;
  }
  return id;
}

function createNewChecklist(){
  const name = (prompt("Nome do novo checklist:") || "").trim();
  if(!name) return;

  // evita duplicar por nome (opcional, mas ajuda)
  if(state.list.some(x => (x.nome || "").trim().toLowerCase() === name.toLowerCase())){
    if(!confirm("Já existe um checklist com esse nome. Criar mesmo assim?")) return;
  }

  const baseId = slugifyId(name);
  const id = makeUniqueId(baseId);

  const chk = { id, nome: name, pasta: "MANUAL", custom: true, active: true, itens: [] };
  state.list.unshift(chk);
  saveDB(state.list);
  setDirty(false);
  selectChecklist(id);
  renderSidebar();
  renderEditor();
}

function promptLastNL(){
  const current = getLastNL();
  const val = (prompt("Último número de documento (apenas controle):", current) || "").trim();
  if(val === "") return;
  setLastNL(val);
  renderEditor();
}

function manageChecklists(){
  const chk = getSelected();
  if(!chk){
    alert("Selecione um checklist primeiro.");
    return;
  }
  if(!chk.custom){
    alert("Este checklist veio do Excel (base). Para manter o sistema estável, ele não pode ser excluído/desativado aqui.");
    return;
  }

  const status = (chk.active === false) ? "DESATIVADO" : "ATIVO";
  const opt = (prompt(
`Gerenciar checklist: ${chk.nome}\nStatus: ${status}\n\nDigite uma opção:\n1) Desativar\n2) Reativar\n3) Excluir definitivamente\n4) Reativar um checklist desativado (lista)\n\n(Deixe em branco para cancelar)`
  ) || "").trim();

  if(!opt) return;

  if(opt === "1"){
    if(chk.active === false) { alert("Este checklist já está desativado."); return; }
    if(!confirm(`Desativar "${chk.nome}"? Ele ficará oculto, mas poderá ser reativado depois.`)) return;
    chk.active = false;
    saveDB(state.list);
    // seleciona outro ativo
    const next = state.list.find(x => isActiveChecklist(x));
    state.selectedId = next ? next.id : null;
    renderSidebar();
    renderEditor();
    return;
  }

  if(opt === "2"){
    if(chk.active !== false) { alert("Este checklist já está ativo."); return; }
    chk.active = true;
    saveDB(state.list);
    renderSidebar();
    renderEditor();
    return;
  }

  if(opt === "3"){
    if(!confirm(`EXCLUIR DEFINITIVAMENTE "${chk.nome}"?\n\nIsso não tem volta.`)) return;
    const idx = state.list.findIndex(x => x.id === chk.id);
    if(idx >= 0) state.list.splice(idx, 1);
    saveDB(state.list);
    const next = state.list.find(x => isActiveChecklist(x));
    state.selectedId = next ? next.id : null;
    renderSidebar();
    renderEditor();
    return;
  }

  if(opt === "4"){
    const inactives = state.list.filter(x => x.custom && x.active === false);
    if(!inactives.length){
      alert("Não há checklists desativados.");
      return;
    }
    const listText = inactives.map((x,i)=> `${i+1}) ${x.nome}`).join("\n");
    const pick = (prompt(`Checklists desativados:\n\n${listText}\n\nDigite o número para reativar:`) || "").trim();
    const n = parseInt(pick, 10);
    if(!n || n<1 || n>inactives.length) return;
    inactives[n-1].active = true;
    saveDB(state.list);
    renderSidebar();
    return;
  }

  alert("Opção inválida.");
}


