function isActiveChecklist(chk){
  // Por padrão, checklist é ativo. Só os "custom" podem ser desativados.
  if(!chk) return true;
  if(chk.custom) return chk.active !== false;
  return true;
}


function setDirty(v){ state.dirty = !!v; }

function renderSidebar(){
  const box = $("checklist-list");
  if(!box) return;
  const q = ( $("search")?.value || "" ).trim().toLowerCase();

  const filtered = state.list.filter(chk => {
    // Esconde checklists manuais desativados
    if(chk && chk.custom && chk.active === false) return false;
    if(!q) return true;
    const inName = norm(chk.nome).toLowerCase().includes(q);
    if(inName) return true;
    // busca em itens (texto / modelo / ordem / obs)
    return (chk.itens || []).some(it =>
      norm(it.texto).toLowerCase().includes(q) ||
      norm(it.modelo).toLowerCase().includes(q) ||
      norm(it.ordem).toLowerCase().includes(q) ||
      norm(it.obs).toLowerCase().includes(q)
    );
  });

  box.innerHTML = "";
  filtered.forEach(chk => {
    const card = document.createElement("div");
    card.className = "chk-card" + (chk.id === state.selectedId ? " active" : "");
    card.addEventListener("click", () => selectChecklist(chk.id));

    const t = document.createElement("div");
    t.className = "chk-title";
    t.textContent = chk.nome || "(sem nome)";

    const sub = document.createElement("div");
    sub.className = "chk-sub";
    const total = (chk.itens || []).length;
    sub.textContent = `${chk.pasta || ""} • ${total} linhas`;

    card.appendChild(t);
    card.appendChild(sub);
    box.appendChild(card);
  });
}

