function resetToSeed(){
  if(!confirm("Isso vai descartar suas edições locais e recarregar a base do Excel (base). Checklists MANUAIS serão preservados. Continuar?")) return;

  const manual = (state.list || []).filter(x => x && x.custom === true);
  const baseList = clone(SEED);

  // garante que IDs não colidam com a base
  const all = [...baseList];
  manual.forEach(m => {
    if(!m || !m.id) return;
    if(all.some(x => x.id === m.id)){
      const newId = (m.id + "_MANUAL");
      m.id = all.some(x => x.id === newId) ? makeUniqueId(newId) : newId;
    }
    all.push(m);
  });

  state.list = all;
  saveDB(state.list);
  setDirty(false);
  renderSidebar();
  renderEditor();
}

