function splitLines(doc, text, width){
  const t = norm(text).replace(/\r/g,"");
  return doc.splitTextToSize(t, width);
}

function drawJustified(doc, text, x, y, width, lineHeight){
  const paragraphs = norm(text).split(/\n+/);
  let yy = y;
  paragraphs.forEach((para, pIdx) => {
    const words = para.trim().split(/\s+/).filter(Boolean);
    if(words.length === 0){
      yy += lineHeight;
      return;
    }
    // monta linhas por largura
    let line = [];
    let lineW = 0;
    const spaceW = doc.getTextWidth(" ");
    const flushLine = (isLast) => {
      if(line.length === 0) return;
      if(line.length === 1 || isLast){
        doc.text(line.join(" "), x, yy);
      } else {
        const wordsW = line.reduce((acc,w)=>acc+doc.getTextWidth(w),0);
        const gaps = line.length - 1;
        const extra = Math.max(0, width - wordsW);
        const gapW = extra / gaps;
        let xx = x;
        line.forEach((w, i) => {
          doc.text(w, xx, yy);
          xx += doc.getTextWidth(w) + spaceW + gapW;
        });
      }
      yy += lineHeight;
      line = [];
      lineW = 0;
    };
    words.forEach((w, i) => {
      const wW = doc.getTextWidth(w);
      const nextW = (line.length ? lineW + spaceW + wW : lineW + wW);
      if(nextW <= width){
        line.push(w);
        lineW = nextW;
      } else {
        flushLine(false);
        line.push(w);
        lineW = wW;
      }
    });
    flushLine(true);
    if(pIdx < paragraphs.length - 1) yy += lineHeight * 0.2;
  });
  return yy;
}

function addHeaderFooter(doc, pageNum, pageCount, titleText){
  const pageW = doc.internal.pageSize.getWidth();
  const pageH = doc.internal.pageSize.getHeight();

  // header
  const margin = 12;
  const headerH = 18;

  // logo
  try{
    doc.addImage(LOGO_DATAURL, "PNG", margin, 6, 18, 18);
  }catch(e){}

  doc.setFont("helvetica", "bold");
  doc.setFontSize(12);
  doc.text("Quartz Gestão Pública", margin + 22, 14);

  doc.setFont("helvetica", "normal");
  doc.setFontSize(10);
  doc.text(titleText || "", margin + 22, 20);

  doc.setFontSize(9);
  doc.text(`Página ${pageNum} / ${pageCount}`, pageW - margin, 14, {align:"right"});

  // footer
  const footerY = pageH - 18;
  doc.setFontSize(8);
  let y = footerY;
  FOOTER_LINES.forEach((line) => {
    doc.text(line, pageW/2, y, {align:"center"});
    y += 4;
  });
}

