function escapeHtml(s){
  return norm(s).replace(/[&<>"']/g, m => ({"&":"&amp;","<":"&lt;",">":"&gt;","\"":"&quot;","'":"&#39;"}[m]));
}


// ---------- Backup / Restore (localStorage) ----------
// Backup gera um .json com:
// - Banco principal (checklists)
// - Controle "Último NL"
// Serve para: segurança, transferir para outro PC/navegador, e também para manter uma "base" fora do navegador.
function buildBackupObject(){
  const createdAt = new Date().toISOString();
  const payload = {
    app: "Quartz Checklist Studio",
    schema: 1,
    createdAt,
    data: {
      checklists: loadDB() || [],
      lastNL: getLastNL()
    }
  };
  return payload;
}

function downloadJson(filename, obj){
  const blob = new Blob([JSON.stringify(obj, null, 2)], { type: "application/json;charset=utf-8" });
  const url = URL.createObjectURL(blob);
  const a = document.createElement("a");
  a.href = url;
  a.download = filename;
  document.body.appendChild(a);
  a.click();
  a.remove();
  setTimeout(() => URL.revokeObjectURL(url), 600);
}

function doBackup(){
  try{
    const payload = buildBackupObject();
    const ts = payload.createdAt.replace(/[:.]/g,"-");
    downloadJson(`quartz_backup_${ts}.json`, payload);
  }catch(e){
    alert("Não foi possível gerar o backup. " + (e?.message || e));
  }
}

function doRestoreFromFile(file){
  return new Promise((resolve, reject) => {
    const fr = new FileReader();
    fr.onerror = () => reject(new Error("Falha ao ler o arquivo."));
    fr.onload = () => {
      try{
        const raw = fr.result;
        const obj = JSON.parse(raw);
        const list = obj?.data?.checklists;
        const lastNL = obj?.data?.lastNL;

        if(!Array.isArray(list)) throw new Error("Backup inválido: lista de checklists ausente.");
        // validação leve para evitar sobrescrever com lixo
        for(const chk of list){
          if(!chk || typeof chk !== "object") throw new Error("Backup inválido: checklist malformado.");
          if(typeof chk.id !== "string") throw new Error("Backup inválido: checklist sem id.");
          if(!Array.isArray(chk.itens)) chk.itens = [];
        }

        localStorage.setItem(DB_KEY, JSON.stringify(list));
        if(typeof lastNL === "string") setLastNL(lastNL);

        resolve(true);
      }catch(e){
        reject(e);
      }
    };
    fr.readAsText(file);
  });
}

function doRestore(){
  const fileInput = document.createElement("input");
  fileInput.type = "file";
  fileInput.accept = "application/json,.json";
  fileInput.style.display = "none";

  fileInput.addEventListener("change", async () => {
    const file = fileInput.files && fileInput.files[0];
    if(!file) return;

    const ok = confirm(
      "ATENÇÃO: restaurar vai substituir os dados SALVOS neste navegador.\n\n" +
      "Deseja continuar?"
    );
    if(!ok) return;

    try{
      await doRestoreFromFile(file);
      alert("Restauração concluída. O sistema vai recarregar.");
      location.reload();
    }catch(e){
      alert("Não foi possível restaurar. " + (e?.message || e));
    }
  });

  document.body.appendChild(fileInput);
  fileInput.click();
  setTimeout(() => fileInput.remove(), 800);
}

function ensureTopbarBackupButtons(){
  const topbar = document.querySelector(".topbar-right");
  if(!topbar) return;

  // Se já existe (ex.: você colocou no index.html), só garante listener.
  let btnBackup = document.getElementById("btn-backup");
  let btnRestore = document.getElementById("btn-restore");

  if(!btnBackup){
    btnBackup = document.createElement("button");
    btnBackup.id = "btn-backup";
    btnBackup.className = "btn-secondary";
    btnBackup.type = "button";
    btnBackup.textContent = "Backup";
    const btnLogout = document.getElementById("btn-logout");
    if(btnLogout) topbar.insertBefore(btnBackup, btnLogout);
    else topbar.appendChild(btnBackup);
  }

  if(!btnRestore){
    btnRestore = document.createElement("button");
    btnRestore.id = "btn-restore";
    btnRestore.className = "btn-secondary";
    btnRestore.type = "button";
    btnRestore.textContent = "Restaurar";
    const btnLogout = document.getElementById("btn-logout");
    if(btnLogout) topbar.insertBefore(btnRestore, btnLogout);
    else topbar.appendChild(btnRestore);
  }

  // Evita duplicar listeners
  if(!btnBackup.dataset.bound){
    btnBackup.addEventListener("click", doBackup);
    btnBackup.dataset.bound = "1";
  }
  if(!btnRestore.dataset.bound){
    btnRestore.addEventListener("click", doRestore);
    btnRestore.dataset.bound = "1";
  }
}
