// --- SAFETY: hide "Recarregar do Excel (base)" button to avoid accidental reset (UI-only) ---
(function(){
  function hideReloadExcelButton(){
    try{
      const candidates = Array.from(document.querySelectorAll('button, a, .btn, [role="button"]'));
      candidates.forEach(el=>{
        const t = (el.innerText || el.textContent || "").replace(/\s+/g,' ').trim().toLowerCase();
        if(t.includes('recarregar') && t.includes('excel') && t.includes('base')){
          el.style.display = 'none';
          el.setAttribute('aria-hidden','true');
        }
      });
    }catch(e){}
  }

  document.addEventListener('DOMContentLoaded', () => {
    hideReloadExcelButton();
    // Some parts render after load; observe DOM changes to keep it hidden.
    const obs = new MutationObserver(() => hideReloadExcelButton());
    obs.observe(document.body, { childList: true, subtree: true });
    // also run a few times shortly after load
    setTimeout(hideReloadExcelButton, 250);
    setTimeout(hideReloadExcelButton, 800);
    setTimeout(hideReloadExcelButton, 1500);
  });
})();



