// ===============================
const EDIT_PASSWORD = "010349";
const EDIT_LOCK_KEY = "quartz_edit_lock";

if (localStorage.getItem(EDIT_LOCK_KEY) === null) {
  localStorage.setItem(EDIT_LOCK_KEY, "true");
}

function isEditLocked() {
  return localStorage.getItem(EDIT_LOCK_KEY) === "true";
}

function lockEditing() {
  localStorage.setItem(EDIT_LOCK_KEY, "true");
  applyEditLockUI();
  alert("Edição BLOQUEADA (modo consulta).");
}

function unlockEditing() {
  const pwd = prompt("Digite a senha para liberar edição:");
  if (pwd === EDIT_PASSWORD) {
    localStorage.setItem(EDIT_LOCK_KEY, "false");
    applyEditLockUI();
    alert("Edição LIBERADA.");
  } else {
    alert("Senha incorreta.");
  }
}

function applyEditLockUI() {
  const locked = isEditLocked();

  // nunca interfere no login
  if (document.getElementById("login-screen") && 
      document.getElementById("login-screen").style.display !== "none") {
    return;
  }

  document.querySelectorAll("button").forEach(btn => {
    if (["btn-lock-edit","btn-unlock-edit","btn-export-pdf","btn-export-all","btn-logout","btn-backup","btn-restore"].includes(btn.id)) return;
    btn.disabled = locked;
  });

  document.querySelectorAll("input, textarea").forEach(el => {
    if (["search"].includes(el.id)) return;
    el.readOnly = locked;
    if (locked && el.type && el.type !== "text") el.disabled = true;
    if (!locked) el.disabled = false;
  });
}

function injectEditLockButtons() {
  const bar = document.querySelector(".topbar-right");
  if (!bar || document.getElementById("btn-lock-edit")) return;

  const unlock = document.createElement("button");
  unlock.id = "btn-unlock-edit";
  unlock.innerText = "🔓 Liberar edição";
  unlock.onclick = unlockEditing;

  const lock = document.createElement("button");
  lock.id = "btn-lock-edit";
  lock.innerText = "🔒 Bloquear edição";
  lock.onclick = lockEditing;

  bar.prepend(lock);
  bar.prepend(unlock);
}

window.addEventListener("load", () => {
  injectEditLockButtons();
  applyEditLockUI();
});

setInterval(() => {
  injectEditLockButtons();
  applyEditLockUI();
}, 1500);

// Proteção extra: não salvar quando bloqueado
if (typeof saveDB === "function") {
  const _saveDB = saveDB;
  saveDB = function() {
    if (isEditLocked()) {
      alert("Sistema em modo consulta. Edição bloqueada.");
      return;
    }
    return _saveDB.apply(this, arguments);
  };
}
